const fs = require('fs');
// npm install等を利用して、form-dataとaxiosを利用できるようにしてください
const FormData = require('form-data');
const axios = require('axios').default;

const headers  = {
  'Content-Type': 'application/json; charset=utf-8',
  'Authorization': 'Bearer {credential}',
  'User-Agent': 'ua'
};
const params  = {
  name: '{画像ファイル名}',
  content_type: 'image/jpeg',
  size: 24785
};
const filePath = '{画像ファイルのpath}';
const API_URL = 'https://api.stores.dev/retail/202211/items/image_upload';

axios.post(API_URL, params, { headers: headers })
  .then(response => fileUpload(
    response.data.post_endpoint,
    response.data.signature,
    filePath
  )).catch(function(error) {
    console.log(error.response.data);
  });

const fileUpload = (url, signature, filePath) => {
  // アイテム作成・更新APIのimageの値として指定する
  console.log(signature.key);

  const formData = new FormData();
  Object.keys(signature).forEach(k => formData.append(k, signature[k]));

  const file = fs.createReadStream(filePath);
  formData.append('file', file, { knownLength: fs.statSync(filePath).size });

  axios.post(url, formData)
    .then(response => {
      console.log(response.data);
    }).catch(function(error) {
      console.log(error.response.data);
    });
};
